// This function reformats the timeseries data into an array that 
// Chart.js (See http://www.chartjs.org/ ) will plot
// The node-red-dashboard Chart node uses Chart.js 

var starttime = flow.get("start-time");
var endtime = flow.get("end-time");

if( msg.payload.length === 0 ) {
    // The historical database does not contain values for this date range 
    // Reset the chart with a blank array
    msg.payload = [] ;
    return msg;
}

// The Array has this structure, inserting the first element as a template.
var ChartData = [ {"series":["Data"],
                   "data":[[{"x":msg.payload[0].time,"y":msg.payload[0].papp}]],
                   "labels":["Data"]}];

// Start at 1 because we've already added element 0 in the initial array definition
for( var i=1; i < msg.payload.length; i++ ) {
    if( (msg.payload[i].time >= starttime) && (msg.payload[i].time <= endtime) ) {
        // This temperature reading is within the Date Range
        ChartData[0].data[0].push( { "x":msg.payload[i].time,"y":msg.payload[i].papp } );
    }    
}
// Now we need to sort on the time so that we give the Chart node an array in time order
ChartData[0].data[0].sort(function sortNumber(a,b) { return a.x - b.x; });

msg.payload = ChartData;
return msg;
